import React, { useState, useRef } from "react";
import {
  StyleSheet,
  Text,
  View,
  Dimensions,
  Image,
  ScrollView,
} from "react-native";

import { StatusBar } from "expo-status-bar";
import TeamComps from "./teamcomps";
import ChampTier from "./champtier";
import ItemTier from "./itemtier";
import ClassTier from "./classtier";
import OriginTier from "./origintier";

export default function Welcome() {
  const [index, setIndex] = useState(0);
  const refScrollView = useRef(null);
  return (
    <View style={styles.container}>
      <StatusBar style="light" />
      <View style={styles.titleWrapper}>
        <Image style={styles.logo} source={require("../assets/logo.png")} />
        <Text style={styles.title}>TFTLAB</Text>
      </View>
      <View style={styles.navbarWrapper}>
        <Text
          style={[
            styles.navbarTitle,
            index == 0
              ? { color: "#fff", borderBottomColor: "#fff" }
              : { color: "#88a0a7", borderBottomColor: "#123040" },
          ]}
          onPress={() => {
            setIndex(0);
            refScrollView.scrollTo({ x: 0, y: 0, animated: true });
          }}
        >
          comps
        </Text>

        <Text
          style={[
            styles.navbarTitle,
            index == 1
              ? { color: "#fff", borderBottomColor: "#fff" }
              : { color: "#88a0a7", borderBottomColor: "#123040" },
          ]}
          onPress={() => {
            setIndex(1);
          }}
        >
          champs
        </Text>
        <Text
          style={[
            styles.navbarTitle,
            index == 2 ? { color: "#fff" } : { color: "#88a0a7" },
          ]}
        >
          items
        </Text>
        <Text
          style={[
            styles.navbarTitle,
            index == 3 ? { color: "#fff" } : { color: "#88a0a7" },
          ]}
        >
          classes
        </Text>
        <Text
          style={[
            styles.navbarTitle,
            index == 4 ? { color: "#fff" } : { color: "#88a0a7" },
          ]}
        >
          origins
        </Text>
      </View>
      <ScrollView
        horizontal={true}
        showsHorizontalScrollIndicator={false}
        style={{ flex: 1 }}
        pagingEnabled={true}
        ref={refScrollView}
      >
        <TeamComps />
        <ChampTier />
        <ItemTier />
        <ClassTier />
        <OriginTier />
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    paddingTop: 25,
    backgroundColor: "#102531",
  },
  titleWrapper: {
    paddingVertical: 20,
    height: 40,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
  logo: {
    width: 32,
    height: 32,
  },
  title: {
    fontSize: 24,
    fontFamily: "RobotoBlack",
    color: "#fff",
    textShadowColor: "rgba(0, 0, 0, 0.75)",
    textShadowOffset: { width: -0.75, height: 0 },
    textShadowRadius: 5,
    letterSpacing: 3,
  },
  navbarWrapper: {
    flexDirection: "row",
    width: "90%",
    height: 30,
    backgroundColor: "#123040",
    elevation: 5,
  },
  navbarTitle: {
    fontSize: 14,
    fontFamily: "RobotoBold",
    width: (Dimensions.get("window").width * 0.9) / 5,
    height: 30,
    textAlign: "center",
    borderBottomWidth: 2,
  },
  pageTitle: {
    fontSize: 24,
    fontFamily: "RobotoBold",
    color: "#fff",
  },
  page: {
    width: Dimensions.get("window").width * 0.9,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 8,
    margin: Dimensions.get("window").width * 0.03,
  },
  cardWrapper: {
    width: Dimensions.get("window").width * 0.7,
    height: Dimensions.get("window").height * 0.15,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-evenly",
  },
});
