import React, { useState, useEffect } from "react";
import { StyleSheet, View, Dimensions, FlatList, Text } from "react-native";
import TeamComp from "../components/teamcomp";
import Loading from "../components/loading";

const getComps = async (setIsFetched, setComps) => {
  fetch("http://192.168.1.5:3000/api/teamcomp/", {
    method: "GET",
    headers: {
      "Content-Type": "application/x-www-form-urlencoded;charset=UTF-8",
    },
  })
    .then((res) => res.json())
    .then((resJson) => {
      setComps(resJson);
    })
    .catch((error) => console.error(error));
  setIsFetched(true);
};

export default function TeamComps() {
  const [isFetched, setIsFetched] = useState(false);
  const [comps, setComps] = useState(0);
  useEffect(() => {
    if (!isFetched) getComps(setIsFetched, setComps);
  });

  return (
    <View>
      <View style={styles.page}>
        {comps != 0 ? (
          <View>
            <FlatList
              data={comps}
              showsVerticalScrollIndicator={false}
              renderItem={({ item }) => (
                <TeamComp
                  tier={item.tier}
                  name={item.name}
                  champs={item.champs}
                  traits={item.traits}
                />
              )}
              keyExtractor={(item, index) => String(index)}
            />
          </View>
        ) : (
          <Loading />
        )}
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  page: {
    width: Dimensions.get("window").width * 0.9,
    marginHorizontal: Dimensions.get("window").width * 0.05,
    backgroundColor: "#123040",
    alignItems: "center",
    elevation: 5,
  },
});
